  window.changeImage = function(direction) {
    const sound = document.getElementById('cameraSound');
    const blinkElement = document.getElementById('camera-blink');
    
    sound.play();
    blinkElement.style.display = 'block';
    setTimeout(() => {
      blinkElement.style.display = 'none';
    }, sound.duration * 1000);

    const currentImg = document.getElementById('camera-image-current');
    const nextImg = document.getElementById('camera-image-next');

    let newIndex;
    if (direction === 'next') {
      newIndex = currentImageIndex === totalImages ? 1 : currentImageIndex + 1;
    } else {
      newIndex = currentImageIndex === 1 ? totalImages : currentImageIndex - 1;
    }

    nextImg.setAttribute('xlink:href', getImageUrl(newIndex));
    nextImg.style.transition = 'none';
    nextImg.style.transform = direction === 'next' ? 'translateX(100%)' : 'translateX(-100%)';
    nextImg.style.opacity = '1';

    nextImg.getBoundingClientRect();

    nextImg.style.transition = `transform ${transitionDuration}ms ease-in-out`;
    nextImg.style.transform = 'translateX(0)';
    currentImg.style.transition = `transform ${transitionDuration}ms ease-in-out`;
    currentImg.style.transform = direction === 'next' ? 'translateX(-100%)' : 'translateX(100%)';

    setTimeout(() => {
      currentImg.setAttribute('xlink:href', getImageUrl(newIndex));
      currentImg.style.transition = 'none';
      currentImg.style.transform = 'translateX(0)';
      nextImg.style.opacity = '0';
      currentImageIndex = newIndex;

      updateImageData(newIndex);
    }, transitionDuration);
  };

