  function showAsciiArt(event, duration) {
    const speed = duration / originalHTML.length;
    asciiArt.style.display = "block";
    updateAsciiArtPosition(event);
    asciiArt.innerHTML = '';
    let index = 0;
    clearInterval(animationInterval);
    animationInterval = setInterval(() => {
      if (index < originalHTML.length) {
        asciiArt.innerHTML = originalHTML.slice(0, ++index);
      } else {
        clearInterval(animationInterval);
      }
    }, speed);
  }

  function updateAsciiArtPosition(event) {
    asciiArt.style.left = `${event.pageX + 20}px`;
    asciiArt.style.top = `${event.pageY + 20}px`;
  }

  function hideAsciiArt() {
    asciiArt.style.display = "none";
    clearInterval(animationInterval);
  }

  function fadeIn(element, duration) {
    element.style.opacity = 0;
    animateOpacity(element, 0, 1, duration);
  }

  function fadeOut(element, duration) {
    animateOpacity(element, 1, 0, duration);
  }

  function animateOpacity(element, start, end, duration) {
    let opacity = start;
    const step = 50 / duration * (end - start);

    function animate() {
      opacity += step;
      element.style.opacity = Math.max(0, Math.min(1, opacity));
      if ((step > 0 && opacity < end) || (step < 0 || opacity > end)) {
        requestAnimationFrame(animate);
      }
    }

    animate();
  }

